mapper.select:
SELECT
	
FROM
	
WHERE
	itcode = #{itcode,jdbcType=VARCHAR}
AND
	lotteryId = #{lotteryId,jdbcType=INTEGER}

SELECT 
	ticket 
FROM 
	 
WHERE 
	lotteryId = #{lotteryId, jdbcType=INTEGER} 
	AND backup3 = 1 
	AND backup4 = #{backup4, jdbcType=INTEGER}
ORDER BY 
	ticket
DESC;
